import { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

export default function HotelList() {
  const { location } = useParams();
  const [hotels, setHotels] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    console.log("Location param:", location);
    axios.get(`http://localhost:8080/hotels/location/${location}`)
      .then(res => {
        setHotels(res.data);
        console.log("Hotels fetched:", res.data);
      })
      .catch(err => console.error(err));
  }, [location]);

  return (
    <div style={{ maxWidth: "1300px", margin: "20px auto", padding: "20px" }}>
      <h2 style={{ fontSize: "24px", marginBottom: "20px" }}>
        Hotels in {location}
      </h2>

      {hotels.length > 0 ? (
        hotels.map(hotel => (
          <div
            key={hotel.id}
            onClick={() => navigate(`/hotel/${hotel.id}`)}
            style={{
              display: "flex",
              border: "1px solid #ddd",
              borderRadius: "8px",
              overflow: "hidden",
              boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
              cursor: "pointer",
              marginBottom: "20px"
            }}
          >
            <img
              src={hotel.imageUrl || `https://source.unsplash.com/300x200/?hotel,${hotel.name}`}
              alt={hotel.name}
              style={{ width: "250px", height: "170px", objectFit: "cover" }}
            />
            <div style={{ padding: "15px", flex: 1, display: "flex", flexDirection: "column", justifyContent: "center" }}>
              <h3 style={{ fontSize: "20px", fontWeight: "bold", marginBottom: "5px" }}>{hotel.name}</h3>
              <p style={{ marginBottom: "5px" }}>Location: {hotel.location}</p>
              <p style={{ marginBottom: "5px" }}>Address: {hotel.address}</p>
              <p style={{ marginBottom: "5px" }}>Rating: {hotel.rating}</p>
              <p>Phone: {hotel.phoneno}</p>
            </div>
          </div>
        ))
      ) : (
        <p>No hotels found for "{location}".</p>
      )}
    </div>
  );
}
