import { useParams, useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

export default function Booking() {
  const { roomId } = useParams();
  const [userId, setUserId] = useState("");
  const [checkIn, setCheckIn] = useState("");
  const [checkOut, setCheckOut] = useState("");
  const [price, setPrice] = useState("");
  const navigate = useNavigate();

  const handleBooking = () => {
    axios.post("http://localhost:8080/bookings", {
      userId,
      roomId,
      checkInDate: checkIn,
      checkOutDate: checkOut,
      totalPrice: price,
      status: "booked"
    }).then(() => navigate("/"));
  };

  return (
    <div className="p-6 max-w-md mx-auto">
      <h2 className="text-2xl font-bold mb-4">Booking Details</h2>
      <input placeholder="User ID" value={userId} onChange={(e) => setUserId(e.target.value)} className="border p-2 w-full mb-2" />
      <input type="date" value={checkIn} onChange={(e) => setCheckIn(e.target.value)} className="border p-2 w-full mb-2" />
      <input type="date" value={checkOut} onChange={(e) => setCheckOut(e.target.value)} className="border p-2 w-full mb-2" />
      <input placeholder="Total Price" value={price} onChange={(e) => setPrice(e.target.value)} className="border p-2 w-full mb-2" />
      <button onClick={handleBooking} className="bg-green-600 text-white px-4 py-2 rounded">Proceed to Payment</button>
    </div>
  );
}
