import { useNavigate } from "react-router-dom";
import { useState } from "react";

export default function Home() {
  const navigate = useNavigate();
  const [city, setCity] = useState("");

  const handleSearchClick = () => {
    if (city) {
      navigate(`/hotels/${city}`);
    } else {
      alert("Please select a city.");
    }
  };

  return (
    <div>
      <header className="flex justify-between items-center bg-blue-900 p-4 text-white">
        <h1 className="text-xl font-bold">Hotel Booking</h1>
        <div className="flex gap-4">
          <button onClick={() => navigate("/login")} className="hover:underline">Login</button>
          <button onClick={() => navigate("/register")} className="hover:underline">Register</button>
          <button onClick={() => navigate("/about")} className="hover:underline">About</button>
        </div>
      </header>

      <div className="h-[600px] bg-cover bg-center flex flex-col items-center justify-center text-white text-center" style={{ backgroundImage: "url('https://images.unsplash.com/photo-1600585154340-be6161a56a0c?auto=format&fit=crop&w=1600&h=600&q=80')" }}>
        <h2 className="text-3xl mb-2">Search hotels in your destination</h2>
        <p className="mb-4">Enter your dates to see the best prices and hotel deals</p>

        <div className="bg-white p-4 rounded flex flex-wrap gap-2 justify-center max-w-2xl">
          <select className="border p-2 rounded w-48" value={city} onChange={(e) => setCity(e.target.value)}>
            <option value="">Where to go?</option>
            <option value="Hyderabad">Hyderabad</option>
            <option value="Vijayawada">Vijayawada</option>
            <option value="Vizag">Vizag</option>
          </select>
          <input type="date" className="border p-2 rounded w-48" />
          <input type="date" className="border p-2 rounded w-48" />
          <select className="border p-2 rounded w-48">
            <option>2 adults · 0 children · 1 room</option>
            <option>1 adult · 0 children · 1 room</option>
            <option>3 adults · 1 child · 2 rooms</option>
          </select>
          <button onClick={handleSearchClick} className="bg-blue-600 text-white px-4 py-2 rounded">Search</button>
        </div>
      </div>

      <section className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 p-6 bg-white">
        {[
          { city: "Hyderabad", img: "https://wallpaperaccess.com/full/2142440.jpg" },
          { city: "Vijayawada", img: "https://wallpaperaccess.com/full/1992125.jpg" },
          { city: "Vizag", img: "https://wallpapercave.com/wp/wp11030879.jpg" }
        ].map(place => (
          <div key={place.city} className="bg-gray-100 rounded shadow hover:scale-105 transition cursor-pointer">
            <img src={place.img} alt={place.city} className="w-full h-40 object-cover rounded-t" />
            <button onClick={() => navigate(`/hotels/${place.city}`)} className="block w-full text-center bg-blue-900 text-white p-3">Explore Hotels in {place.city}</button>
          </div>
        ))}
      </section>

      <footer className="bg-blue-900 text-white text-center p-4">
        &copy; 2025 Hotel Booking System. All rights reserved.
      </footer>
    </div>
  );
}
