import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

export default function HotelList() {
  const { location } = useParams();
  const [hotels, setHotels] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get(`http://localhost:8080/hotels/location/${location}`)
      .then((res) => setHotels(res.data))
      .catch((err) => console.log(err));
  }, [location]);

  return (
    <div style={{ maxWidth: "1000px", margin: "40px auto", display: "flex", flexDirection: "column", gap: "20px" }}>
      {hotels.map((hotel) => (
        <div
          key={hotel.id}
          onClick={() => navigate(`/hotel/${hotel.id}`)}
          style={{
            display: "flex",
            border: "1px solid #ddd",
            borderRadius: "10px",
            overflow: "hidden",
            boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
            cursor: "pointer",
            transition: "transform 0.2s",
          }}
        >
          {/* Left: Image */}
          <img
            src={
              hotel.imageUrl || `https://source.unsplash.com/300x200/?hotel,${hotel.name}`
            }
            alt={hotel.name}
            style={{ width: "530px", height: "280px", objectFit: "cover" }}
          />

          {/* Right: Details */}
          <div style={{ padding: "15px", flex: 1, display: "flex", flexDirection: "column", justifyContent: "center" }}>
            <h2 style={{ fontSize: "25px", fontWeight: "bold", marginBottom: "2px" }}>{hotel.name}</h2>
            <p style={{ color: "#555", marginBottom: "5px" }}>{hotel.location}</p>
            <p style={{ color: "green", fontSize: "14px", marginBottom: "5px" }}>{hotel.address}</p>

            <p style={{ fontSize: "14px", color: "#555", marginBottom: "2px" }}>
              Rating: <strong>{hotel.rating}</strong>
            </p>
            <p style={{ fontSize: "14px", color: "#555" }}>
              Phone: {hotel.phoneno}
            </p>
          </div>
        </div>
      ))}
    </div>
  );
}
