import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

export default function RoomList() {
  const { hotelId } = useParams();
  const navigate = useNavigate();
  const [rooms, setRooms] = useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:8080/rooms/hotel/${hotelId}`)
      .then((res) => setRooms(res.data))
      .catch((err) => console.log(err));
  }, [hotelId]);

  return (
    <div style={{ maxWidth: "1300px", margin: "20px auto", display: "flex", flexDirection: "column", gap: "20px", fontFamily: "-apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif" }}>
      {rooms.map((room) => (
        <div key={room._id} style={{ display: "flex", border: "1px solid #ddd", borderRadius: "8px", overflow: "hidden", boxShadow: "0 2px 6px rgba(0,0,0,0.1)" }}>
          <img
            src={room.imageUrl}
            alt={room.roomType}
            style={{ width: "480px", height: "230px", objectFit: "cover" }}
          />
          <div style={{ padding: "15px", flex: 1, display: "flex", flexDirection: "column", justifyContent: "center" }}>
            <h3 style={{ fontSize: "20px", fontWeight: "bold", marginBottom: "8px" }}>{room.roomType}</h3>
            <p style={{ marginBottom: "5px" }}><strong>Price:</strong> â‚¹{room.price}</p>
            <div>
              <strong>Amenities:</strong>
              <ul style={{ marginTop: "5px", paddingLeft: "20px" }}>
                {room.eminities && room.eminities.split(",").map((amenity, idx) => (
                  <li key={idx}>{amenity.trim()}</li>
                ))}
              </ul>
            </div>
            <button
              onClick={() => navigate(`/booking/${room._id}`)}
              style={{
                marginTop: "15px",
                padding: "10px",
                backgroundColor: "#0071c2",
                color: "white",
                border: "none",
                cursor: "pointer",
                alignSelf: "flex-start"
              }}
            >
              Book Now
            </button>
          </div>
        </div>
      ))}

      {rooms.length === 0 && <p>No rooms available for this hotel.</p>}
    </div>
  );
}
