import { useNavigate } from "react-router-dom";
import { useState } from "react";

export default function Home() {
  const navigate = useNavigate();
  const [location, setLocation] = useState("");

  return (
    <div className="relative h-screen bg-cover bg-center" style={{ backgroundImage: "url('https://www.lux-review.com/wp-content/uploads/2022/05/5-Star-Holiday-Room.jpg')" }}>
      <div className="absolute inset-0 bg-black bg-opacity-50 flex flex-col justify-center items-center">
        <h1 className="text-4xl md:text-6xl font-bold text-white mb-4">Where to next?</h1>
        <div className="bg-white rounded-2xl p-4 flex flex-col md:flex-row gap-2 shadow w-11/12 md:w-2/3">
          <input placeholder="Where to?" className="p-2 rounded border flex-1" value={location} onChange={(e) => setLocation(e.target.value)} />
          <input type="date" className="p-2 rounded border flex-1" />
          <input type="date" className="p-2 rounded border flex-1" />
          <button onClick={() => navigate(`/hotels/${location}`)} className="bg-blue-600 text-white px-4 py-2 rounded">Search</button>
        </div>
      </div>
      <div className="absolute bottom-10 w-full flex justify-center gap-4">
        {["Hyderabad", "Vizag", "Visakhapatnam"].map(city => (
          <button key={city} onClick={() => navigate(`/hotels/${city}`)} className="bg-white px-4 py-2 rounded shadow hover:bg-gray-100">{city}</button>
        ))}
      </div>
    </div>
  );
}
