import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

export default function HotelList() {
  const { location } = useParams();
  const [hotels, setHotels] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get(`http://localhost:8080/hotels/location/${location}`)
      .then((res) => setHotels(res.data))
      .catch((err) => console.log(err));
  }, [location]);

  return (
    <div className="p-6 max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
      {hotels.map((hotel) => (
        <div
          key={hotel.id}
          className="border rounded-xl overflow-hidden shadow-lg bg-white hover:shadow-2xl cursor-pointer transition"
          onClick={() => navigate(`/hotel/${hotel.id}`)}
        >
          <img
            src={
              hotel.imageUrl ||
              `https://source.unsplash.com/500x300/?hotel,${hotel.name}`
            }
            alt={hotel.name}
            className="w-full h-48 object-cover"
          />

          <div className="p-4 flex flex-col justify-between h-full">
            <h2 className="text-lg font-bold text-gray-900 mb-1">
              {hotel.name}
            </h2>
            <p className="text-gray-600 text-sm mb-2">{hotel.location}</p>

            <div className="flex items-center justify-between mt-2">
              <span className="text-green-600 font-medium">
                Fully refundable
              </span>
              {hotel.rating && (
                <div className="bg-green-600 text-white px-2 py-1 rounded-md text-sm font-semibold">
                  {hotel.rating}
                </div>
              )}
            </div>

            <p className="text-gray-700 mt-3 text-sm">Phone: {hotel.phoneno}</p>
          </div>
        </div>
      ))}
    </div>
  );
}
