import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function Bookings() {
  const navigate = useNavigate();
  const [bookings, setBookings] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);

    if (!storedUser || !storedUser.id) {
      return;
    }

    axios
      .get(`http://localhost:8080/booking/user/${storedUser.id}`)
      .then((res) => setBookings(res.data))
      .catch((err) => console.log(err));
  }, []);

  if (!user || !user.id) {
    return (
      <div style={styles.centerWrapper}>
        <div style={styles.box}>
          <h3>Please login first to view your bookings</h3>
          <button onClick={() => navigate("/login")} style={styles.button}>
            Go to Login
          </button>
        </div>
      </div>
    );
  }

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "0 auto" }}>
      <h2>My Bookings</h2>
      {bookings.length === 0 ? (
        <p>You have no bookings yet.</p>
      ) : (
        bookings.map((b) => (
          <div key={b.id} style={styles.bookingCard}>
            <p><strong>Room:</strong> {b.roomId}</p>
            <p><strong>Check-in:</strong> {b.checkInDate}</p>
            <p><strong>Check-out:</strong> {b.checkOutDate}</p>
            <p><strong>Total Price:</strong> â‚¹{b.totalPrice}</p>
          </div>
        ))
      )}
    </div>
  );
}

const styles = {
  centerWrapper: {
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    background: "white",
  },
  box: {
    background: "white",
    padding: "40px",
    borderRadius: "12px",
    boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
    textAlign: "center",
  },
  button: {
    marginTop: "15px",
    padding: "10px 20px",
    backgroundColor: "#0071c2",
    color: "white",
    border: "none",
    cursor: "pointer",
    borderRadius: "4px",
  },
  bookingCard: {
    border: "1px solid #ddd",
    borderRadius: "8px",
    padding: "15px",
    marginBottom: "15px",
    backgroundColor: "#f9f9f9",
  },
};
