import { useParams, useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

export default function BookingPage() {
  const { roomId } = useParams();
  const navigate = useNavigate();

  const [userId, setUserId] = useState(""); // You can fetch logged-in user ID if available
  const [checkInDate, setCheckInDate] = useState("");
  const [checkOutDate, setCheckOutDate] = useState("");
  const [totalPrice, setTotalPrice] = useState("");
  const [message, setMessage] = useState("");

  const handleBooking = (e) => {
    e.preventDefault();

    if (!userId || !checkInDate || !checkOutDate || !totalPrice) {
      setMessage("Please fill all fields");
      return;
    }

    const bookingDetails = {
      userId,
      roomId,
      checkInDate,
      checkOutDate,
      totalPrice,
      status: "booked",
    };

    axios
      .post("http://localhost:8080/bookings", bookingDetails)
      .then(() => {
        alert("Room booked successfully!");
        navigate("/");
      })
      .catch(() => setMessage("Booking failed. Please try again."));
  };

  return (
    <div
      style={{
        maxWidth: "500px",
        margin: "50px auto",
        padding: "30px",
        border: "1px solid #ddd",
        borderRadius: "8px",
        boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
        fontFamily: "-apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
      }}
    >
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>Book Your Room</h2>

      {message && <p style={{ color: "red", textAlign: "center" }}>{message}</p>}

      <form onSubmit={handleBooking} style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
        
        <input
          type="text"
          placeholder="Your User ID"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
          style={{ padding: "12px", borderRadius: "6px", border: "1px solid #ccc" }}
          required
        />

        <label>Check-In Date:</label>
        <input
          type="date"
          value={checkInDate}
          onChange={(e) => setCheckInDate(e.target.value)}
          style={{ padding: "12px", borderRadius: "6px", border: "1px solid #ccc" }}
          required
        />

        <label>Check-Out Date:</label>
        <input
          type="date"
          value={checkOutDate}
          onChange={(e) => setCheckOutDate(e.target.value)}
          style={{ padding: "12px", borderRadius: "6px", border: "1px solid #ccc" }}
          required
        />

        <input
          type="number"
          placeholder="Total Price"
          value={totalPrice}
          onChange={(e) => setTotalPrice(e.target.value)}
          style={{ padding: "12px", borderRadius: "6px", border: "1px solid #ccc" }}
          required
        />

        <button
          type="submit"
          style={{
            padding: "12px",
            backgroundColor: "#0071c2",
            color: "white",
            border: "none",
            borderRadius: "6px",
            fontWeight: "bold",
            cursor: "pointer",
          }}
        >
          Confirm Booking
        </button>
      </form>
    </div>
  );
}
