import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

export default function HotelList() {
  const { location } = useParams();
  const [hotels, setHotels] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get(`http://localhost:8080/hotels/location/${location}`)
      .then((res) => setHotels(res.data))
      .catch((err) => console.log(err));
  }, [location]);

  return (
    <div className="p-6 max-w-5xl mx-auto flex flex-col gap-6">
      {hotels.map((hotel) => (
        <div
          key={hotel.id}
          className="flex border rounded-xl overflow-hidden shadow-md bg-white hover:shadow-lg cursor-pointer transition"
          onClick={() => navigate(`/hotel/${hotel.id}`)}
        >
          {/* Image section with fixed width */}
          <img
            src={
              hotel.imageUrl ||
              `https://source.unsplash.com/300x200/?hotel,${hotel.name}`
            }
            alt={hotel.name}
            className="w-60 h-44 object-cover"
          />

          {/* Details section */}
          <div className="p-4 flex flex-col justify-between flex-grow">
            <div>
              <h2 className="text-lg font-bold text-gray-900 mb-1">
                {hotel.name}
              </h2>
              <p className="text-gray-600 text-sm mb-2">{hotel.location}</p>
              <p className="text-green-700 text-sm mb-1">Fully refundable</p>
              {hotel.rating && (
                <div className="mt-1 flex items-center gap-2">
                  <span
                    className={`px-2 py-1 rounded-md text-white text-xs font-semibold ${
                      hotel.rating >= 9
                        ? "bg-green-600"
                        : hotel.rating >= 7
                        ? "bg-yellow-500"
                        : "bg-red-500"
                    }`}
                  >
                    {hotel.rating}
                  </span>
                  <span className="text-gray-700 text-sm">
                    {hotel.rating >= 9
                      ? "Wonderful"
                      : hotel.rating >= 7
                      ? "Good"
                      : "Average"}
                  </span>
                </div>
              )}
            </div>
            <p className="text-gray-700 text-sm mt-2">Phone: {hotel.phoneno}</p>
          </div>
        </div>
      ))}
    </div>
  );
}
