import { useState, useEffect, useCallback } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

export default function HotelList() {
  const { location } = useParams();
  const [hotels, setHotels] = useState([]);
  const [searchName, setSearchName] = useState("");
  const [minRating, setMinRating] = useState("");
  const [selectedAddress, setSelectedAddress] = useState("");
  const navigate = useNavigate();

  const popularAreas = ["Hitech City", "Shamshabad", "Konda Nagar", "Madhuranagar", "Banjara Hills"];

  const fetchHotelsByLocation = useCallback(() => {
    axios.get(`http://localhost:8080/hotels/location/${location}`)
      .then(res => setHotels(res.data))
      .catch(err => console.log(err));
  }, [location]);

  useEffect(() => {
    fetchHotelsByLocation();
  }, [fetchHotelsByLocation]);

  const handleFilter = () => {
    if (searchName.trim()) {
      axios.get(`http://localhost:8080/hotels/name/${searchName}`)
        .then(res => setHotels(res.data))
        .catch(err => console.log(err));
    } else if (minRating) {
      axios.get(`http://localhost:8080/hotels/filterByRating`, {
        params: { minRating }
      })
        .then(res => setHotels(res.data))
        .catch(err => console.log(err));
    } else if (selectedAddress) {
      axios.get(`http://localhost:8080/hotels/address/${selectedAddress}`)
        .then(res => setHotels(res.data))
        .catch(err => console.log(err));
    } else {
      fetchHotelsByLocation();
    }
  };

  return (
    <div style={{ display: "flex", maxWidth: "1300px", margin: "20px auto" }}>
      
      {/* Filter Sidebar */}
      <div style={{ width: "250px", padding: "30px", borderRight: "1px solid #ddd" }}>
        
        <h3>Property Name</h3>
        <input
          type="text"
          placeholder="e.g. Marriott"
          value={searchName}
          onChange={(e) => setSearchName(e.target.value)}
          style={{ width: "100%", padding: "8px", marginBottom: "15px" }}
        />

        <h3>Filter by Rating</h3>
        {[1, 2, 3, 4, 5].map(rating => (
          <div key={rating} style={{ marginBottom: "8px" }}>
            <input
              type="radio"
              id={`rating${rating}`}
              name="rating"
              value={rating}
              checked={minRating === String(rating)}
              onChange={(e) => setMinRating(e.target.value)}
            />
            <label htmlFor={`rating${rating}`} style={{ marginLeft: "5px" }}>
              {rating}+
            </label>
          </div>
        ))}

        <h3>Filter by Popular Areas</h3>
        {popularAreas.map(area => (
          <div key={area} style={{ marginBottom: "8px" }}>
            <input
              type="checkbox"
              id={area}
              name="address"
              value={area}
              checked={selectedAddress === area}
              onChange={(e) => setSelectedAddress(e.target.value)}
            />
            <label htmlFor={area} style={{ marginLeft: "8px" }}>{area}</label>
          </div>
        ))}

        <button
          onClick={handleFilter}
          style={{ width: "100%", padding: "10px", backgroundColor: "#0071c2", color: "white", border: "none", cursor: "pointer", marginTop: "15px" }}
        >
          Apply Filter
        </button>
      </div>

      {/* Hotel List */}
      <div style={{ flex: 1, padding: "20px", display: "flex", flexDirection: "column", gap: "20px" }}>
        {hotels.map(hotel => (
          <div
            key={hotel.id}
            onClick={() => navigate(`/hotel/${hotel.id}`)}
            style={{
              display: "flex",
              border: "1px solid #ddd",
              borderRadius: "8px",
              overflow: "hidden",
              boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
              cursor: "pointer",
            }}
          >
            <img
              src={hotel.imageUrl || `https://source.unsplash.com/300x200/?hotel,${hotel.name}`}
              alt={hotel.name}
              style={{ width: "500px", height: "250px", objectFit: "cover" }}
            />
            <div style={{ padding: "15px", flex: 1, display: "flex", flexDirection: "column", justifyContent: "center" }}>
              <h2 style={{ fontSize: "25px", fontWeight: "bold", marginBottom: "5px" }}>{hotel.name}</h2>
              <p style={{ color: "#555", marginBottom: "5px" }}>{hotel.location}</p>
              <p style={{ color: "green", fontSize: "18px", marginBottom: "5px" }}>{hotel.address}</p>
              <p style={{ fontSize: "16px", color: "#555" }}>
                Rating: <strong>{hotel.rating}</strong>
              </p>
              <p style={{ fontSize: "16px", color: "#555" }}>
                Phone: {hotel.phoneno}
              </p>
            </div>
          </div>
        ))}
      </div>

    </div>
  );
}
