import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

export default function HotelList() {
  const { location } = useParams();
  const [hotels, setHotels] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:8080/hotels/location/${location}`).then(res => setHotels(res.data));
  }, [location]);

  return (
    <div className="p-4 grid grid-cols-1 md:grid-cols-3 gap-4">
      {hotels.map(hotel => (
        <div key={hotel.id} className="bg-white rounded-2xl shadow-lg overflow-hidden cursor-pointer" onClick={() => navigate(`/hotel/${hotel.id}`)}>
          <img src={`https://source.unsplash.com/400x200/?hotel,${hotel.name}`} alt={hotel.name} className="w-full h-48 object-cover" />
          <div className="p-4">
            <h2 className="text-xl font-bold">{hotel.name}</h2>
            <p className="text-gray-600">{hotel.location}</p>
            <p className="text-gray-800 mt-2">{hotel.rating}</p>
            <p className="text-blue-600 font-semibold mt-2"> {hotel.phoneno} </p>
          </div>
        </div>
      ))}
    </div>
  );
}
