import { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

export default function Booking() {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const [room, setRoom] = useState(null);
  const [checkInDate, setCheckInDate] = useState("");
  const [checkOutDate, setCheckOutDate] = useState("");
  const [showLoginAlert, setShowLoginAlert] = useState(false);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user) {
      setShowLoginAlert(true);
      return;
    }

    if (roomId) {
      axios
        .get(`http://localhost:8080/rooms/${roomId}`)
        .then((res) => setRoom(res.data))
        .catch((err) => console.log(err));
    }
  }, [roomId]);

  const calculateTotalPrice = () => {
    if (!checkInDate || !checkOutDate || !room) return 0;
    const start = new Date(checkInDate);
    const end = new Date(checkOutDate);
    const diffTime = end - start;
    const diffDays = diffTime / (1000 * 60 * 60 * 24);
    return diffDays > 0 ? diffDays * room.price : 0;
  };

  const handleBooking = () => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user) {
      setShowLoginAlert(true);
      return;
    }

    if (!checkInDate || !checkOutDate) {
      alert("Please select both check-in and check-out dates");
      return;
    }

    axios
      .post("http://localhost:8080/booking/book", null, {
        params: {
          userId: user.id,
          roomId: roomId,
          checkInDate: checkInDate,
          checkOutDate: checkOutDate,
        },
      })
      .then(() => {
        alert("Room booked successfully!");
        navigate("/");
      })
      .catch((err) => alert(err.response?.data || "Booking failed"));
  };

  if (showLoginAlert) {
    return (
      <div style={overlayStyle}>
        <div style={modalStyle}>
          <h3>Please Login to Book a Room</h3>
          <button
            onClick={() => navigate("/login")}
            style={{
              marginTop: "15px",
              padding: "10px",
              backgroundColor: "#0071c2",
              color: "white",
              border: "none",
              cursor: "pointer",
              borderRadius: "4px",
            }}
          >
            Go to Login
          </button>
        </div>
      </div>
    );
  }

  if (!room) return <p>Loading room details...</p>;

  return (
    <div style={{ maxWidth: "600px", margin: "20px auto" }}>
      <h2>Booking for: {room.roomType}</h2>
      <p>Price per night: ₹{room.price}</p>
      <input
        type="date"
        value={checkInDate}
        onChange={(e) => setCheckInDate(e.target.value)}
        style={{ marginRight: "10px" }}
      />
      <input
        type="date"
        value={checkOutDate}
        onChange={(e) => setCheckOutDate(e.target.value)}
      />
      <br />
      <br />
      <p>Total Price: ₹{calculateTotalPrice()}</p>
      <button
        onClick={handleBooking}
        style={{
          padding: "10px",
          backgroundColor: "#0071c2",
          color: "white",
          border: "none",
          cursor: "pointer",
        }}
      >
        Book Now
      </button>
    </div>
  );
}

const overlayStyle = {
  position: "fixed",
  inset: 0,
  backgroundColor: "rgba(0,0,0,0.4)",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  zIndex: 1000,
};

const modalStyle = {
  background: "white",
  padding: "30px",
  borderRadius: "8px",
  textAlign: "center",
  boxShadow: "0 4px 10px rgba(0,0,0,0.2)",
};
