import { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import Home from './Home'; // We import the Home component to use as the background

export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Function to close the modal by navigating back to the home page
  const handleClose = () => {
    navigate('/');
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');

    if (!email || !password) {
      setError('Please enter both email and password.');
      return;
    }

    try {
      const response = await fetch('http://localhost:8080/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => null);
        throw new Error(errorData?.message || 'Invalid credentials.');
      }

      const userData = await response.json();
      localStorage.setItem('user', JSON.stringify(userData));
      navigate('/'); // Redirect to home on successful login

    } catch (err) {
      setError(err.message);
    }
  };

  const pageStyles = `
    .login-container {
      position: relative;
      width: 100%;
      height: 100vh;
      overflow: hidden; /* Prevents scrolling of the background */
    }

    /* The modal overlay that dims the background */
    .modal-overlay {
      position: fixed;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      background-color: rgba(0, 0, 0, 0.5);
      display: flex;
      justify-content: center;
      align-items: center;
      z-index: 1000;
    }

    /* The actual login form box */
    .login-box {
      background: white;
      padding: 30px;
      border-radius: 12px;
      box-shadow: 0 5px 15px rgba(0,0,0,0.3);
      width: 110%;
      max-width: 400px;
      text-align: center;
      z-index: 1001;
    }

    .login-box h2 {
      font-size: 24px;
      font-weight: bold;
      margin-bottom: 25px;
      color: #333;
    }

    .login-box input {
      width: 100%;
      padding: 12px;
      margin-bottom: 15px;
      border: 1px solid #ddd;
      border-radius: 8px;
      font-size: 16px;
    }
    
    .login-box input::placeholder {
      color: #999;
    }

    .login-box button {
      width: 100%;
      padding: 12px;
      border: none;
      border-radius: 8px;
      background-color: rgba(47, 133, 141, 1); /* Teal color from screenshot */
      color: white;
      font-size: 16px;
      font-weight: bold;
      cursor: pointer;
      transition: background-color 0.2s;
    }

    .login-box button:hover {
      background-color: rgba(38, 110, 115, 1);
    }

    .register-link {
      margin-top: 20px;
      font-size: 14px;
      color: #555;
    }

    .register-link a {
      color: rgba(47, 133, 141, 1);
      font-weight: bold;
      text-decoration: none;
    }
    
    .register-link a:hover {
        text-decoration: underline;
    }

    .error-message {
        color: #e53e3e;
        font-size: 14px;
        margin-bottom: 15px;
    }
  `;

  return (
    <>
      <style>{pageStyles}</style>
      <div className="login-container">
        {/* Render the Home component in the background */}
        <Home />

        {/* The Modal Overlay */}
        <div className="modal-overlay" onClick={handleClose}>
          <div className="login-box" onClick={(e) => e.stopPropagation()}>
            <form onSubmit={handleLogin}>
              <h2>Login</h2>
              <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              {error && <p className="error-message">{error}</p>}
              <button type="submit">Login</button>
              <p className="register-link">
                New user? <Link to="/register">Register</Link>
              </p>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}
