import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function HotelList() {
  const [hotels, setHotels] = useState([]);
  const [searchName, setSearchName] = useState("");
  const [minRating, setMinRating] = useState("");
  const [minPrice, setMinPrice] = useState("");
  const [maxPrice, setMaxPrice] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    axios.get("http://localhost:8080/hotels/location/Hyderabad")
      .then(res => setHotels(res.data))
      .catch(err => console.log(err));
  }, []);

  const handleSearch = () => {
    if (searchName.trim()) {
      axios.get(`http://localhost:8080/hotels/name/${searchName}`)
        .then(res => setHotels(res.data))
        .catch(err => console.log(err));
    } else if (minPrice && maxPrice) {
      axios.get("http://localhost:8080/hotels/filterByPrice", { params: { minPrice, maxPrice } })
        .then(res => setHotels(res.data))
        .catch(err => console.log(err));
    } else if (minRating) {
      axios.get("http://localhost:8080/hotels/filterByRating", { params: { minRating } })
        .then(res => setHotels(res.data))
        .catch(err => console.log(err));
    } else {
      alert("Please apply at least one filter.");
    }
  };

  return (
    <div style={{ display: "flex", maxWidth: "1300px", margin: "20px auto" }}>

      {/* Left Sidebar Filters */}
      <div style={{ width: "250px", padding: "20px", borderRight: "1px solid #ddd" }}>
        <h3>Search by Property Name</h3>
        <input
          type="text"
          placeholder="e.g. Marriott"
          value={searchName}
          onChange={(e) => setSearchName(e.target.value)}
          style={{ width: "100%", padding: "8px", marginBottom: "15px" }}
        />

        <h4>Filter by Rating</h4>
        <select
          value={minRating}
          onChange={(e) => setMinRating(e.target.value)}
          style={{ width: "100%", padding: "8px", marginBottom: "15px" }}
        >
          <option value="">Any Rating</option>
          <option value="1">1+</option>
          <option value="2">2+</option>
          <option value="3">3+</option>
          <option value="4">4+</option>
          <option value="5">5</option>
        </select>

        <h4>Nightly Price</h4>
        <input
          type="number"
          placeholder="Min Price"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
          style={{ width: "100%", padding: "8px", marginBottom: "10px" }}
        />
        <input
          type="number"
          placeholder="Max Price"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
          style={{ width: "100%", padding: "8px", marginBottom: "20px" }}
        />

        <button
          onClick={handleSearch}
          style={{ width: "100%", padding: "10px", backgroundColor: "#0071c2", color: "white", border: "none", cursor: "pointer" }}
        >
          Apply Filters
        </button>
      </div>

      {/* Hotel List Right Side */}
      <div style={{ flex: 1, padding: "20px", display: "flex", flexDirection: "column", gap: "20px" }}>
        {hotels.map(hotel => (
          <div
            key={hotel.id}
            onClick={() => navigate(`/hotel/${hotel.id}`)}
            style={{
              display: "flex",
              border: "1px solid #ddd",
              borderRadius: "8px",
              overflow: "hidden",
              boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
              cursor: "pointer",
              transition: "transform 0.2s"
            }}
          >
            {/* Image */}
            <img
              src={hotel.imageUrl || `https://source.unsplash.com/300x200/?hotel,${hotel.name}`}
              alt={hotel.name}
              style={{ width: "250px", height: "170px", objectFit: "cover" }}
            />

            {/* Hotel Details */}
            <div style={{ padding: "15px", flex: 1, display: "flex", flexDirection: "column", justifyContent: "space-between" }}>
              <div>
                <h2 style={{ fontSize: "20px", fontWeight: "bold", marginBottom: "5px" }}>{hotel.name}</h2>
                <p style={{ color: "#555", marginBottom: "5px" }}>{hotel.location}</p>
                <p style={{ color: "green", fontSize: "14px", marginBottom: "5px" }}>{hotel.address}</p>
                <p style={{ fontSize: "14px", color: "#555", marginBottom: "5px" }}>
                  Rating: <strong>{hotel.rating}</strong>
                </p>
              </div>
              <div style={{ textAlign: "right" }}>
                <h3 style={{ fontSize: "18px", color: "#0071c2" }}>
                  â‚¹{hotel.avgPrice || "N/A"}
                </h3>
                <p style={{ fontSize: "12px", color: "#777" }}>includes taxes & fees</p>
              </div>
            </div>
          </div>
        ))}
      </div>

    </div>
  );
}
