function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    axios.post("http://localhost:8080/auth/login", { email, password })
      .then(res => {
        if (res.data) {
          setMessage("Login successful");
          navigate("/");
        } else {
          setMessage("Invalid credentials");
        }
      })
      .catch(() => setMessage("Login failed"));
  };

  return (
    <div className="p-6 max-w-md mx-auto">
      <h2 className="text-2xl font-bold mb-4">Login</h2>
      <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} className="border p-2 w-full mb-2" />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="border p-2 w-full mb-2" />
      <button onClick={handleLogin} className="bg-blue-600 text-white px-4 py-2 rounded w-full">Login</button>
      {message && <p className="text-center text-red-500 mt-2">{message}</p>}
    </div>
  );
}
export default Login;
