import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

export default function RoomList() {
  const { hotelId } = useParams();
  const [rooms, setRooms] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:8080/rooms/hotel/${hotelId}`).then(res => setRooms(res.data));
  }, [hotelId]);

  return (
    <div className="p-4 grid grid-cols-1 md:grid-cols-3 gap-4">
      {rooms.map(room => (
        <div key={room.roomid} className="bg-white rounded-2xl shadow-lg overflow-hidden">
          <img src={`https://source.unsplash.com/400x200/?hotel-room,${room.roomType}`} alt={room.roomType} className="w-full h-48 object-cover" />
          <div className="p-4">
            <h2 className="text-xl font-bold">{room.roomType}</h2>
            <p className="text-gray-800">â‚¹ {room.price} / night</p>
            <p className="text-green-600">{room.available ? "Available" : "Not Available"}</p>
            <button onClick={() => navigate(`/book/${room.roomid}`)} className="mt-2 bg-blue-600 text-white px-4 py-2 rounded">Book</button>
          </div>
        </div>
      ))}
    </div>
  );
}
