import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const BookingsPage = () => {
  const navigate = useNavigate();
  const { hotelId } = useParams();
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user || user.role !== "ADMIN") {
      navigate("/");
    } else {
      fetchBookings();
    }
  }, [navigate, hotelId]);

  const fetchBookings = async () => {
    try {
      const response = await fetch(`http://localhost:8080/bookings/hotel/${hotelId}`);
      const data = await response.json();
      setBookings(data);
    } catch (error) {
      console.error("Error fetching bookings:", error);
    }
  };

  return (
    <div style={styles.container}>
      <h2>Bookings for Hotel ID: {hotelId}</h2>
      {bookings.length === 0 ? (
        <p>No bookings found for this hotel.</p>
      ) : (
        <div style={styles.bookingList}>
          {bookings.map((booking) => (
            <div key={booking.id} style={styles.card}>
              <h3>Booking ID: {booking.id}</h3>
              <p><strong>User:</strong> {booking.userName}</p>
              <p><strong>Room No:</strong> {booking.roomNumber}</p>
              <p><strong>Check-in:</strong> {booking.checkInDate}</p>
              <p><strong>Check-out:</strong> {booking.checkOutDate}</p>
              <p><strong>Total Price:</strong> â‚¹{booking.totalPrice}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: "20px",
    fontFamily: "Arial, sans-serif",
  },
  bookingList: {
    display: "flex",
    flexDirection: "column",
    gap: "20px",
  },
  card: {
    border: "1px solid #ccc",
    padding: "15px",
    borderRadius: "10px",
    backgroundColor: "#fff",
  },
};

export default BookingsPage;
