import { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

export default function HotelList() {
  const { location } = useParams();
  const [hotels, setHotels] = useState([]);
  const [searchName, setSearchName] = useState("");
  const [minRating, setMinRating] = useState("");
  const [selectedAreas, setSelectedAreas] = useState([]);
  const navigate = useNavigate();

  const popularAreas = ["Hitech City", "Shamshabad", "Konda Nagar", "Madhuranagar", "Banjara Hills"];

  useEffect(() => {
    fetchHotelsByLocation();
  }, [location]);

  const fetchHotelsByLocation = () => {
    axios.get(`http://localhost:8080/hotels/location/${location}`)
      .then(res => setHotels(res.data))
      .catch(err => console.log(err));
  };

  const handleAreaChange = (e) => {
    const { value, checked } = e.target;
    if (checked) {
      setSelectedAreas([...selectedAreas, value]);
    } else {
      setSelectedAreas(selectedAreas.filter(area => area !== value));
    }
  };

  const handleFilter = () => {
    if (searchName.trim()) {
      axios.get(`http://localhost:8080/hotels/name/${searchName}`)
        .then(res => setHotels(res.data))
        .catch(err => console.log(err));
    } else if (minRating) {
      axios.get(`http://localhost:8080/hotels/filterByRating`, {
        params: { minRating }
      })
        .then(res => setHotels(res.data))
        .catch(err => console.log(err));
    } else if (selectedAreas.length > 0) {
      axios.get(`http://localhost:8080/hotels/addresses`, {
        params: { areas: selectedAreas.join(",") }
      })
        .then(res => setHotels(res.data))
        .catch(err => console.log(err));
    } else {
      fetchHotelsByLocation();
    }
  };

  return (
    <div style={{ display: "flex", maxWidth: "1300px", margin: "20px auto" }}>
      
      {/* Filter Sidebar */}
      <div style={{ width: "250px", padding: "20px", borderRight: "1px solid #ddd" }}>
        
        <h3>Property Name</h3>
        <input
          type="text"
          placeholder="e.g. Marriott"
          value={searchName}
          onChange={(e) => setSearchName(e.target.value)}
          style={{ width: "100%", padding: "8px", marginBottom: "15px" }}
        />

        <h4>Filter by Rating</h4>
        <select
          value={minRating}
          onChange={(e) => setMinRating(e.target.value)}
          style={{ width: "100%", padding: "8px", marginBottom: "15px" }}
        >
          <option value="">Any Rating</option>
          <option value="1">1+</option>
          <option value="2">2+</option>
          <option value="3">3+</option>
          <option value="4">4+</option>
          <option value="5">5</option>
        </select>

        <h4>Filter by Popular Areas</h4>
        {popularAreas.map(area => (
          <div key={area} style={{ marginBottom: "8px" }}>
            <input
              type="checkbox"
              id={area}
              value={area}
              checked={selectedAreas.includes(area)}
              onChange={handleAreaChange}
            />
            <label htmlFor={area} style={{ marginLeft: "8px" }}>{area}</label>
          </div>
        ))}

        <button
          onClick={handleFilter}
          style={{ width: "100%", padding: "10px", backgroundColor: "#0071c2", color: "white", border: "none", cursor: "pointer", marginTop: "15px" }}
        >
          Apply Filter
        </button>
      </div>

      {/* Hotel List */}
      <div style={{ flex: 1, padding: "20px", display: "flex", flexDirection: "column", gap: "20px" }}>
        {hotels.map(hotel => (
          <div
            key={hotel.id}
            onClick={() => navigate(`/hotel/${hotel.id}`)}
            style={{
              display: "flex",
              border: "1px solid #ddd",
              borderRadius: "8px",
              overflow: "hidden",
              boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
              cursor: "pointer",
            }}
          >
            <img
              src={hotel.imageUrl || `https://source.unsplash.com/300x200/?hotel,${hotel.name}`}
              alt={hotel.name}
              style={{ width: "250px", height: "170px", objectFit: "cover" }}
            />
            <div style={{ padding: "15px", flex: 1, display: "flex", flexDirection: "column", justifyContent: "center" }}>
              <h2 style={{ fontSize: "20px", fontWeight: "bold", marginBottom: "5px" }}>{hotel.name}</h2>
              <p style={{ color: "#555", marginBottom: "5px" }}>{hotel.location}</p>
              <p style={{ color: "green", fontSize: "14px", marginBottom: "5px" }}>{hotel.address}</p>
              <p style={{ fontSize: "14px", color: "#555" }}>
                Rating: <strong>{hotel.rating}</strong>
              </p>
              <p style={{ fontSize: "14px", color: "#555" }}>
                Phone: {hotel.phoneno}
              </p>
            </div>
          </div>
        ))}
      </div>

    </div>
  );
}
