import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

export default function HotelList() {
  const { location } = useParams();
  const [hotels, setHotels] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get(`http://localhost:8080/hotels/location/${location}`)
      .then((res) => setHotels(res.data))
      .catch((err) => console.log(err));
  }, [location]);

  return (
    <div className="p-6 max-w-4xl mx-auto flex flex-col gap-4">
      {hotels.map((hotel) => (
        <div
          key={hotel.id}
          className="flex border rounded-lg shadow bg-white overflow-hidden hover:shadow-lg cursor-pointer transition"
          onClick={() => navigate(`/hotel/${hotel.id}`)}
        >
          {/* Left: Image */}
          <img
            src={
              hotel.imageUrl ||
              `https://source.unsplash.com/300x200/?hotel,${hotel.name}`
            }
            alt={hotel.name}
            style={{ width: "250px", height: "180px", objectFit: "cover" }}
          />

          {/* Right: Details */}
          <div className="p-4 flex flex-col justify-between flex-grow">
            <div>
              <h2 className="text-xl font-bold mb-1">{hotel.name}</h2>
              <p className="text-gray-600 mb-2">{hotel.location}</p>

              <div className="flex flex-wrap items-center gap-2 mb-2">
                <span className="text-green-700 text-sm">Fully refundable</span>
                {hotel.rating && (
                  <span
                    className={`px-2 py-1 rounded text-white text-xs font-semibold ${
                      hotel.rating >= 9
                        ? "bg-green-600"
                        : hotel.rating >= 7
                        ? "bg-yellow-500"
                        : "bg-red-500"
                    }`}
                  >
                    {hotel.rating} Rating
                  </span>
                )}
              </div>

              <p className="text-gray-700 text-sm">Phone: {hotel.phoneno}</p>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}
