import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function MyBookings() {
  const navigate = useNavigate();
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    // eslint-disable-next-line
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser || !storedUser.id) {
      alert("Please login to view your bookings");
      navigate("/login");
      return;
    }

    axios
      .get(`http://localhost:8080/booking/user/${storedUser.id}`)
      .then((res) => setBookings(res.data))
      .catch((err) => console.log(err));
  }, [navigate]);

  return (
    <>
      <style>
        {`
          body { background-color: white; margin: 0; font-family: Arial, sans-serif; }
          .bookings-wrapper { max-width: 800px; margin: 40px auto; padding: 20px; }
          .booking-card { border: 1px solid #e2e8f0; border-radius: 8px; padding: 20px; margin-bottom: 20px; box-shadow: 0 2px 8px rgba(0,0,0,0.05); }
          .booking-card h3 { margin-bottom: 10px; color: #2d3748; }
          .booking-card p { margin: 5px 0; color: #4a5568; }
        `}
      </style>

      <div className="bookings-wrapper">
        <h2>Your Bookings</h2>

        {bookings.length === 0 ? (
          <p>You have no bookings yet.</p>
        ) : (
          bookings.map((booking) => (
            <div className="booking-card" key={booking.id}>
              <h3>Room Type: {booking.roomType || booking.roomId}</h3>
              <p>Check-In Date: {booking.checkInDate}</p>
              <p>Check-Out Date: {booking.checkOutDate}</p>
              <p>Total Price: â‚¹{booking.totalPrice}</p>
              <p>Status: {booking.status}</p>
            </div>
          ))
        )}
      </div>
    </>
  );
}
