import { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

export default function Booking() {
  const { roomId } = useParams();
  const navigate = useNavigate();

  const [room, setRoom] = useState(null);
  const [checkInDate, setCheckInDate] = useState("");
  const [checkOutDate, setCheckOutDate] = useState("");
  const [showSuccessPopup, setShowSuccessPopup] = useState(false);

  const user = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    if (!user) {
      alert("Please login to book rooms");
      navigate("/login");
      return;
    }

    if (roomId) {
      axios
        .get(`http://localhost:8080/rooms/${roomId}`)
        .then((res) => setRoom(res.data))
        .catch((err) => console.error("Error fetching room:", err));
    }
  }, [roomId, navigate, user]);

  const calculateTotalPrice = () => {
    if (!checkInDate || !checkOutDate || !room) return 0;

    const start = new Date(checkInDate);
    const end = new Date(checkOutDate);
    const diffTime = end - start;
    const diffDays = diffTime / (1000 * 60 * 60 * 24);

    return diffDays > 0 ? diffDays * room.price : 0;
  };

  const handleBooking = () => {
    if (!checkInDate || !checkOutDate) {
      alert("Please select both check-in and check-out dates");
      return;
    }

    const start = new Date(checkInDate);
    const end = new Date(checkOutDate);

    if (start >= end) {
      alert("Check-out date must be after check-in date");
      return;
    }

    axios
      .post("http://localhost:8080/booking/book", null, {
        params: {
          userId: user.id,
          roomId: roomId,
          checkInDate: checkInDate,
          checkOutDate: checkOutDate,
        },
      })
      .then(() => setShowSuccessPopup(true))
      .catch((err) => alert(err.response?.data || "Booking failed"));
  };

  return (
    <>
      <div className="booking-wrapper">
        {room ? (
          <div className="booking-box">
            <h2>Book {room.roomType}</h2>
            <p>Price per night: ₹{room.price}</p>

            <input
              type="date"
              value={checkInDate}
              onChange={(e) => setCheckInDate(e.target.value)}
              required
            />

            <input
              type="date"
              value={checkOutDate}
              onChange={(e) => setCheckOutDate(e.target.value)}
              required
            />

            <p>Total Price: ₹{calculateTotalPrice()}</p>

            <button onClick={handleBooking}>Confirm Booking</button>
          </div>
        ) : (
          <p>Loading room details...</p>
        )}
      </div>

      {showSuccessPopup && (
        <div className="overlay">
          <div className="popup">
            <h3>Room Booked Successfully!</h3>
            <button
              onClick={() => navigate("/mybookings")}
              style={{
                marginTop: "15px",
                padding: "10px",
                backgroundColor: "#0071c2",
                color: "white",
                border: "none",
                cursor: "pointer",
                borderRadius: "4px",
              }}
            >
              Go to My Bookings
            </button>
          </div>
        </div>
      )}
    </>
  );
}
