import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const RoomPage = () => {
  const { hotelId } = useParams();
  const navigate = useNavigate();
  const [rooms, setRooms] = useState([]);
  const [roomForm, setRoomForm] = useState({
    roomType: "",
    price: "",
    amenities: "",
    imageUrl: ""
  });
  const [editingRoomId, setEditingRoomId] = useState(null);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user || user.role !== "ADMIN") {
      navigate("/");
    } else {
      fetchRooms();
    }
  }, [navigate, hotelId]);

  const fetchRooms = async () => {
    try {
      const response = await fetch(`http://localhost:8080/rooms/hotel/${hotelId}`);
      const data = await response.json();
      setRooms(data);
    } catch (error) {
      console.error("Error fetching rooms:", error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setRoomForm({ ...roomForm, [name]: value });
  };

  const handleSubmit = async () => {
    try {
      const user = JSON.parse(localStorage.getItem("user"));
      const method = editingRoomId ? "PUT" : "POST";
      const url = editingRoomId
        ? `http://localhost:8080/rooms/${editingRoomId}?userId=${user.id}`
        : `http://localhost:8080/rooms?userId=${user.id}`;

      const roomData = { ...roomForm, hotelId };

      const response = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(roomData),
      });

      if (!response.ok) throw new Error("Operation failed");

      setRoomForm({ roomType: "", price: "", amenities: "", imageUrl: "" });
      setEditingRoomId(null);
      fetchRooms();
    } catch (error) {
      console.error("Error:", error);
    }
  };

  const handleEditRoom = (room) => {
    setRoomForm({
      roomType: room.roomType,
      price: room.price,
      amenities: room.amenities,
      imageUrl: room.imageUrl
    });
    setEditingRoomId(room.id);
  };

  const handleDeleteRoom = async (id) => {
    try {
      const user = JSON.parse(localStorage.getItem("user"));
      const response = await fetch(`http://localhost:8080/rooms/${id}?userId=${user.id}`, {
        method: "DELETE",
      });
      if (!response.ok) throw new Error("Delete failed");
      fetchRooms();
    } catch (error) {
      console.error("Delete error:", error);
    }
  };

  return (
    <div style={styles.container}>
      {/* Form Section */}
      <div style={styles.leftPane}>
        <h2>{editingRoomId ? "Update Room" : "Add Room"}</h2>
        <form style={styles.form}>
          <input name="roomType" value={roomForm.roomType} onChange={handleInputChange} placeholder="Room Type" required />
          <input name="price" type="number" value={roomForm.price} onChange={handleInputChange} placeholder="Price" required />
          <input name="amenities" value={roomForm.amenities} onChange={handleInputChange} placeholder="Amenities" required />
          <input name="imageUrl" value={roomForm.imageUrl} onChange={handleInputChange} placeholder="Image URL" required />
          <button type="button" onClick={handleSubmit} style={styles.button}>
            {editingRoomId ? "Update" : "Add"} Room
          </button>
        </form>
      </div>

      {/* Room List Section */}
      <div style={styles.rightPane}>
        <h2>Existing Rooms</h2>
        <div style={styles.roomList}>
          {rooms.map((room) => (
            <div key={room.id} style={styles.card}>
              <img src={room.imageUrl} alt={room.roomType} style={styles.image} />
              <div style={styles.details}>
                <h3>{room.roomType}</h3>
                <p><strong>Price:</strong> â‚¹{room.price}</p>
                <p><strong>Amenities:</strong> {room.amenities}</p>
                <div style={styles.actionButtons}>
                  <button onClick={() => handleEditRoom(room)} style={styles.editBtn}>Edit</button>
                  <button onClick={() => handleDeleteRoom(room.id)} style={styles.deleteBtn}>Delete</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    padding: "20px",
    fontFamily: "Arial, sans-serif",
  },
  leftPane: {
    flex: 1,
    marginRight: "40px",
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "10px",
    background: "#f9f9f9",
  },
  rightPane: {
    flex: 2,
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "10px",
  },
  button: {
    padding: "10px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontWeight: "bold",
  },
  roomList: {
    display: "flex",
    flexDirection: "column",
    gap: "20px",
  },
  card: {
    display: "flex",
    border: "1px solid #ccc",
    padding: "15px",
    borderRadius: "10px",
    backgroundColor: "#fff",
  },
  image: {
    width: "160px",
    height: "100px",
    objectFit: "cover",
    marginRight: "20px",
    borderRadius: "5px",
  },
  details: {
    flex: 1,
  },
  actionButtons: {
    marginTop: "10px",
    display: "flex",
    gap: "10px",
  },
  editBtn: {
    padding: "5px 10px",
    backgroundColor: "#28a745",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
  },
  deleteBtn: {
    padding: "5px 10px",
    backgroundColor: "#dc3545",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
  }
};

export default RoomPage;
