import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function RoomList() {
  const { hotelId } = useParams();
  const [rooms, setRooms] = useState([]);
  const [filteredRooms, setFilteredRooms] = useState([]);
  const [maxPrice, setMaxPrice] = useState("");
  const [selectedAmenities, setSelectedAmenities] = useState([]);
  const [selectedRoomType, setSelectedRoomType] = useState("");

  const availableAmenities = ["Wi-Fi", "AC", "TV", "Parking", "Breakfast"];
  const roomTypes = ["Single", "Double", "Suite", "Deluxe"];

  useEffect(() => {
    fetchRooms();
  }, [hotelId]);

  const fetchRooms = () => {
    axios
      .get(`http://localhost:8080/rooms/hotel/${hotelId}`)
      .then((res) => {
        setRooms(res.data);
        setFilteredRooms(res.data);
      })
      .catch((err) => console.log(err));
  };

  const handleFilter = () => {
    let filtered = [...rooms];

    if (maxPrice) {
      filtered = filtered.filter((room) => room.price <= parseFloat(maxPrice));
    }

    if (selectedRoomType) {
      filtered = filtered.filter((room) => room.roomType === selectedRoomType);
    }

    if (selectedAmenities.length > 0) {
      filtered = filtered.filter((room) =>
        selectedAmenities.every((amenity) => room.amenities?.includes(amenity))
      );
    }

    setFilteredRooms(filtered);
  };

  const handleAmenityChange = (e) => {
    const value = e.target.value;
    if (selectedAmenities.includes(value)) {
      setSelectedAmenities(selectedAmenities.filter((a) => a !== value));
    } else {
      setSelectedAmenities([...selectedAmenities, value]);
    }
  };

  const handleReset = () => {
    setMaxPrice("");
    setSelectedAmenities([]);
    setSelectedRoomType("");
    setFilteredRooms(rooms);
  };

  return (
    <div style={{ maxWidth: "1300px", margin: "20px auto", display: "flex", fontFamily: "-apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif" }}>
      
      {/* Filter Sidebar */}
      <div style={{ width: "250px", padding: "20px", borderRight: "1px solid #ddd" }}>
        <h3>Max Price</h3>
        <input
          type="number"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
          placeholder="e.g. 3000"
          style={{ width: "100%", padding: "8px", marginBottom: "15px" }}
        />

        <h3>Room Type</h3>
        {roomTypes.map((type) => (
          <div key={type} style={{ marginBottom: "8px" }}>
            <input
              type="radio"
              id={type}
              name="roomType"
              value={type}
              checked={selectedRoomType === type}
              onChange={(e) => setSelectedRoomType(e.target.value)}
            />
            <label htmlFor={type} style={{ marginLeft: "8px" }}>{type}</label>
          </div>
        ))}

        <h3 style={{ marginTop: "20px" }}>Amenities</h3>
        {availableAmenities.map((amenity) => (
          <div key={amenity} style={{ marginBottom: "8px" }}>
            <input
              type="checkbox"
              id={amenity}
              value={amenity}
              checked={selectedAmenities.includes(amenity)}
              onChange={handleAmenityChange}
            />
            <label htmlFor={amenity} style={{ marginLeft: "8px" }}>{amenity}</label>
          </div>
        ))}

        <button
          onClick={handleFilter}
          style={{ width: "100%", padding: "10px", backgroundColor: "#0071c2", color: "white", border: "none", marginTop: "15px", cursor: "pointer" }}
        >
          Apply Filter
        </button>
        <button
          onClick={handleReset}
          style={{ width: "100%", padding: "10px", backgroundColor: "#aaa", color: "white", border: "none", marginTop: "10px", cursor: "pointer" }}
        >
          Reset
        </button>
      </div>

      {/* Room List */}
      <div style={{ flex: 1, padding: "20px", display: "flex", flexDirection: "column", gap: "15px" }}>
        {filteredRooms.map((room) => (
          <div key={room.id} style={{ border: "1px solid #ddd", padding: "15px", borderRadius: "8px", boxShadow: "0 2px 6px rgba(0,0,0,0.1)" }}>
            <h3>{room.roomType}</h3>
            <p><strong>Price:</strong> ₹{room.price}</p>
            <p><strong>Amenities:</strong> {room.amenities?.join(", ") || "N/A"}</p>
          </div>
        ))}

        {filteredRooms.length === 0 && <p>No rooms found matching your criteria.</p>}
      </div>
    </div>
  );
}
